import React, { useState } from 'react';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { InputGroup, Input } from 'reactstrap';
import { Link } from 'react-router-dom';

type DashboardPlayerTypeahead = {
  firstName: string,
  lastName: string,
  playerAbbr: string
}

type DashboardPlayerQueryResponse = {
  loading: boolean;
  data: {
    dashPlayerTypeahead: [DashboardPlayerTypeahead];
  }
}

export const GET_PLAYER_ABBR = gql`
  query GetPlayerAbbrFromName($lastName: String){
    dashPlayerTypeahead(lastName: $lastName) {
      firstName,
      lastName,
      playerAbbr
    }
  }
`;

const PlayerSelector = () => {
  const [textAreaValue, setTextAreaValue] = useState<string>("");
  return (
    <Query query={GET_PLAYER_ABBR} variables={{ playerAbbr: textAreaValue }}>
      {({ loading, data }: DashboardPlayerQueryResponse) => !loading && (
        <div>
          <InputGroup>
            <Input onChange={(event) => setTextAreaValue(event.target.value)}/>
          </InputGroup>
          <ul>
            {data.dashPlayerTypeahead.map((player) => (
              <li key={`li.${player.playerAbbr}`}><Link key={player.playerAbbr} to={`/p/${player.playerAbbr}`}>{player.lastName}, {player.firstName}</Link></li>
            ))}
          </ul>
        </div>
      )}
    </Query>
  );
}

export default PlayerSelector;
