import React, { useState } from 'react';
import { Button, ButtonGroup } from 'reactstrap';

type SeasonSelectorComponentProps = {
  onStateChange: (value: any) => object,
}

export default({ onStateChange }: SeasonSelectorComponentProps) => {
  const [rSelected, setRSelected] = useState<number>(1);

  return (
    <ButtonGroup>
      <Button onClick={() => setRSelected(1)} active={rSelected === 1}>One</Button>
      <Button onClick={() => setRSelected(2)} active={rSelected === 2}>Three</Button>
      <Button onClick={() => setRSelected(3)} active={rSelected === 3}>Lifetime</Button>
    </ButtonGroup>
  )
}
