import React, { useState } from 'react';
import { Button, ButtonGroup } from 'reactstrap';
import { SeasonSelection } from '../types/PlayerDashboardTypes';

type SeasonSelectorComponentProps = {
  onStateChange: (value: SeasonSelection) => object,
}

export default({ onStateChange }: SeasonSelectorComponentProps) => {
  const [value, setValue] = useState<number>(1);

  const changeSelection = (newValue: SeasonSelection) => {
    onStateChange(newValue);
    setValue(newValue);
  }

  return (
    <ButtonGroup>
      <Button onClick={() => changeSelection(SeasonSelection.One)} active={value === 1}>One</Button>
      <Button onClick={() => changeSelection(SeasonSelection.Three)} active={value === 2}>Three</Button>
      <Button onClick={() => changeSelection(SeasonSelection.Lifetime)} active={value === 3}>Lifetime</Button>
    </ButtonGroup>
  )
}
